select movies.title from ((stars inner join people on stars.person_id = people.id) inner join movies on stars.movie_id = movies.id) where people.name = 'Helena Bonham Carter' and movies.title in (select movies.title from ((stars inner join people on stars.person_id = people.id) inner join movies on stars.movie_id = movies.id) where people.name = 'Johnny Depp');